const NewsBox = {
    // backgroundColor: "pink",
    margin: "15px",
    padding: "25px",
    backgroundColor: "white",
    minWidth: "25%",
    boxShadow: "0px 2px 5px rgba(0,0,0,.2)"
}

const Story = [
    {
        headline: "",
        date: "",
        link: "",
        description: "",

    },

]

const NewsStory = {

}

class News extends Component {
    render() {
        return (
            <div style={{ padding: "70px 40px", overflow: "hidden", maxWidth: "80%", margin: "0 auto" }}>
                <div style={{ display: "flex", overflow: "scroll" }}>
                    <div style={NewsBox}>
                        <h3 style={{ margin: 0, fontSize: "25px" }}>Aegis Crypto Custodian Launches And Already Gained Fosun As Lead Investor and Foxconn Client Deal</h3>
                        <p>Feb 27, 2019</p>
                        <p>Holding cryptocurrency is best done through a crypto wallet or through a crypto custodian, considering that their infrastructure is designed to protect assets adequately. </p>
                        <a style={{ color: "#ff6a5f" }} href="https://bitcoinexchangeguide.com/aegis-crypto-custodian-launches-and-already-gained-fosun-as-lead-investor-and-foxconn-client-deal/">READ MORE</a>
                    </div>
                </div>
                <div style={{ display: "flex", overflow: "scroll" }}>
                    {Story.map((story, i) => (
                        <NewsStory>
                            <h3>{story.title}</h3>
                            <p>{story.date}</p>
                            <p>{story.description}</p>
                            <a style={{ color: "#ff6a5f" }} href={story.link}>READ MORE</a>
                        </NewsStory>
                    ))}

                </div>

            </div>
        );
    }
}
export default News;
