const Container = styled.div`
    background: #344854;
    color: #fff;
    padding: 40px 60px;
    display: flex;
    align-items: center;
    flex-wrap: wrap;

    p {
        margin: 0px;
        padding: 0px 20px;
    }

    @media (max-width: 600px) {
        padding: 40px 30px;
        display:none;
    }
`;

const Col = styled.div`
    width: ${props => props.width || "50%"};

    @media (max-width: 1000px) {
        width: 100%;
        margin: 20px auto;
    }
`;

const Coins = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 15px;
    min-width: 400px;

    img {
        height: 75px;
        margin: 0 10px;
        @media (max-width: 1000px) {
            margin-right: 0px;
        }
        ${props => props.coin && "height: 65px;"}
    }

    p {
        ${props =>
        props.support &&
        `
            font-family: gotham_book_italic;
            letter-spacing: 0.2em;
            text-transform: uppercase;
            margin-top: 20px;
            color: #fff;
