import React, { Component } from "react";
import styled from "styled-components";
import Prismic from "prismic-javascript";
import { Link, RichText, Date } from "prismic-reactjs";

const Container = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    max-width: 1000px;
    margin: 0 auto;
    h1 {
        font-family: gotham_book;
    }

    @media (max-width: 1000px) {
        padding: 20px 30px;
    }
`;

const Card = styled.div`
    min-width: 250px;   
    margin: 20px 20px 40px 20px;
    max-width: 200px;
`;

const LinkedIn = styled.div`
    a:hover {
        color: #FF6A5F
    }
`

class OurTeam extends Component {
    state = {
        doc: null
    };

    componentDidMount() {
        this.loadJobs();
    }

    loadJobs = () => {
        const apiEndpoint = "https://aegis.cdn.prismic.io/api/v2";

        Prismic.api(apiEndpoint).then(api => {
            api.query(Prismic.Predicates.at("document.type", "employee")).then(
                response => {
                    if (response) {
                        this.setState({ doc: response.results });
                        console.log(response)
                    }
                }
            );
        });
    };

    // {this.state.doc &&
    //     this.state.doc.map((employee, i) => (
    //         <Card key={i}>
    //             {console.log(employee)}
    //             {/* <Profile url={employee.data.profile.url} /> */}
    //             <h3 style={{ marginBottom: "10px", textAlign: "center" }}>{employee.data.name[0].text}</h3>
    //             <p style={{ margin: "0", textAlign: "center" }}>{employee.data.title[0].text}</p>
    //         </Card>
    //     ))}

    render() {
        return (
            <div id="ourTeam" style={{ maxWidth: "1000px", padding: "70px 0", margin: "0 auto" }}>
                <h2 style={{ textAlign: "center" }}>Our Team</h2>
                <p style={{ maxWidth: "560px", margin: "0 auto 60px", textAlign: "center" }}>Our team is made up of global leaders, financial executives, leading technologists and advisors
                </p>
                <Container>
                    <Card>
                        <div style={{backgroundColor: "#f2f2f2", height: "100px", width: "100px", borderRadius: "100px", margin: "0 auto"}}></div>
                        <LinkedIn><a href="" style={{textDecoration: "none"}}><h3 style={{ marginTop: "20px", marginBottom: "10px", textAlign: "center" }}>Serra Wei</h3></a></LinkedIn>
                        <p style={{ margin: "0", textAlign: "center" }}>Chief Executive Officer</p>
                    </Card>
                    <Card>
                        <div style={{backgroundColor: "#f2f2f2", height: "100px", width: "100px", borderRadius: "100px", margin: "0 auto"}}></div>
                        <LinkedIn><a href="https://www.linkedin.com/in/%E5%86%A0%E5%BD%B0-%E5%82%85-71673472" style={{textDecoration: "none"}}><h3 style={{ marginTop: "20px", marginBottom: "10px", textAlign: "center" }}>Frank Fu</h3></a></LinkedIn>
                        <p style={{ margin: "0", textAlign: "center" }}>Chief Technical Officer</p>
                    </Card>
                    <Card>
                        <div style={{backgroundColor: "#f2f2f2", height: "100px", width: "100px", borderRadius: "100px", margin: "0 auto"}}></div>
                        <LinkedIn><a href="" style={{textDecoration: "none"}}><h3 style={{ marginTop: "20px", marginBottom: "10px", textAlign: "center" }}>Jason Doll</h3></a></LinkedIn>
                        <p style={{ margin: "0", textAlign: "center" }}>Head of Capital Markets</p>
                    </Card>
                    <Card>
                        <div style={{backgroundColor: "#f2f2f2", height: "100px", width: "100px", borderRadius: "100px", margin: "0 auto"}}></div>
                        <LinkedIn><a href="" style={{textDecoration: "none"}}><h3 style={{ marginTop: "20px", marginBottom: "10px", textAlign: "center" }}>Patrick Baron</h3></a></LinkedIn>
                        <p style={{ margin: "0", textAlign: "center" }}>Advisor</p>
                    </Card>
                    <Card>
                        <div style={{backgroundColor: "#f2f2f2", height: "100px", width: "100px", borderRadius: "100px", margin: "0 auto"}}></div>
                        <LinkedIn><a href="https://www.linkedin.com/in/jen-hao-waterball-liu-7152256a/" style={{textDecoration: "none"}}><h3 style={{ marginTop: "20px", marginBottom: "10px", textAlign: "center" }}>Waterball Liu</h3></a></LinkedIn>
                        <p style={{ margin: "0", textAlign: "center" }}>Technical Marketing Manager</p>
                    </Card>
                    <Card>
                        <div style={{backgroundColor: "#f2f2f2", height: "100px", width: "100px", borderRadius: "100px", margin: "0 auto"}}></div>
                        <LinkedIn><a href="" style={{textDecoration: "none"}}><h3 style={{ marginTop: "20px", marginBottom: "10px", textAlign: "center" }}>Sean Chung</h3></a></LinkedIn>
                        <p style={{ margin: "0", textAlign: "center" }}>Engineer</p>
                    </Card>
                </Container>
            </div>
        );
    }
}
export default OurTeam;
